userCSS:
alspotron-wrapper {
  min-height: 400px;

  flex-flow: row !important;
  gap: 32px;

  background: linear-gradient(
    315deg,
    rgba(0, 0, 0, 0.5) 0%,
    rgba(0, 0, 0, 0) 25%
  );

  padding: 32px;
}

alspotron-lyrics-item {
  border-radius: 8px;
  padding: 4px 8px;
}


alspotron-nowplaying {
  width: 150px;
  height: 150px;
  max-width: 150px;

  border-radius: 12px;
  flex-shrink: 0;

  position: relative;
  display: inline-flex;
  flex-flow: column;
  justify-content: flex-end;
  align-items: flex-start;

  padding: 0;
}
alspotron-nowplaying::after {
  content: '';
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;

  background-image: linear-gradient(
    0deg,
    rgba(0, 0, 0, 0.75) 0%,
    rgba(0, 0, 0, 0.5) 24px,
    rgba(0, 0, 0, 0) 48px
  );
  z-index: 20;
}


alspotron-nowplaying-progress-bar {
  background: none !important;
  z-index: 21;
}
alspotron-nowplaying-progress {
  --color: 29, 29, 29;

  opacity: 0.75;

  background-image: linear-gradient(
    135deg,
    rgba(var(--color), 1) 0%,
    rgba(var(--color), 1) 25%,
    rgba(var(--color), 0) 25%,
    rgba(var(--color), 0) 50%,
    rgba(var(--color), 1) 50%,
    rgba(var(--color), 1) 75%,
    rgba(var(--color), 0) 75%,
    rgba(var(--color), 0) 100%
  );
  background-size: 24px 24px;

  animation: progress 1.5s linear infinite;
}

@keyframes progress {
  0% {
    background-position-x: 0;
  }
  100% {
    background-position-x: 24px;
  }
}

alspotron-nowplaying-cover {
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  z-index: -1;

  object-fit: cover;
  border-radius: 12px;
  padding: 4px;

}

alspotron-wrapper--stopped alspotron-nowplaying-cover {
  transform: scale(0.9);
}

alspotron-nowplaying-playing-text {
  position: relative;
  z-index: 50;
  margin-top: auto;
  padding: 12px 16px;
  overflow: visible !important;
}

alspotron-marquee {
  z-index: 50;
}

alspotron-nowplaying-container {
  width: 100%;
}
